let current=0;for(var i=0;i<document.links.length;i++)document.links[i].href===document.URL&&(current=i);document.links[current].className="active";const header=document.querySelector(".site-header"),navOverlay=document.querySelector(".nav-overlay"),primaryMenu=document.querySelector("#primary-menu"),btnHamburger=document.querySelector("#menu-toggle");btnHamburger.addEventListener("click",(function(e){e.preventDefault(),header.classList.contains("open")?(btnHamburger.classList.remove("open"),header.classList.remove("open"),navOverlay.classList.remove("fade-in"),navOverlay.classList.add("fade-out"),primaryMenu.classList.remove("fade-in"),primaryMenu.classList.add("fade-out"),primaryMenu.style.display="none"):(btnHamburger.classList.add("open"),header.classList.add("open"),navOverlay.classList.remove("fade-out"),navOverlay.classList.add("fade-in"),primaryMenu.classList.remove("fade-out"),primaryMenu.classList.add("fade-in"),primaryMenu.style.display="block")}));let sticky=header.offsetTop;function slimHeader(){window.pageYOffset>sticky?header.classList.add("slim"):header.classList.remove("slim")}function getCopyrightYear(){document.getElementById("year").innerHTML=(new Date).getFullYear()}window.onscroll=function(){slimHeader()()},getCopyrightYear();