const header=document.querySelector(".site-header"),navOverlay=document.querySelector(".nav-overlay"),primaryMenu=document.querySelector("#primary-menu"),btnHamburger=document.querySelector("#menu-toggle");function stickyHeader(){0==window.pageYOffset?(header.classList.remove("sticky"),header.classList.add("transparent")):window.pageYOffset>95&&window.pageYOffset<200?header.classList.remove("transparent"):window.pageYOffset>=200&&(header.classList.remove("transparent"),header.classList.add("sticky"))}btnHamburger.addEventListener("click",(function(e){e.preventDefault(),header.classList.contains("open")?(document.body.classList.remove("modal-open"),btnHamburger.classList.remove("open"),header.classList.remove("open"),navOverlay.classList.remove("fade-in"),navOverlay.classList.add("fade-out"),primaryMenu.classList.remove("fade-in"),primaryMenu.classList.add("fade-out"),primaryMenu.style.display="none"):(document.body.classList.add("modal-open"),btnHamburger.classList.add("open"),header.classList.add("open"),navOverlay.classList.remove("fade-out"),navOverlay.classList.add("fade-in"),primaryMenu.classList.remove("fade-out"),primaryMenu.classList.add("fade-in"),primaryMenu.style.display="block")}));const socialIconBar=document.querySelector(".social-icon-bar");function handleSocialIconBar(){window.pageYOffset>200&&socialIconBar.classList.add("in-left")}function getCopyrightYear(){document.getElementById("year").innerHTML=(new Date).getFullYear()}document.addEventListener("scroll",(function(e){e.preventDefault(),stickyHeader(),handleSocialIconBar()})),getCopyrightYear();