function getCopyrightYear(){document.getElementById("year").innerHTML=(new Date).getFullYear()}getCopyrightYear();let header=document.getElementById("masthead"),sticky=header.offsetTop;function slimHeader(){window.pageYOffset>sticky?header.classList.add("slim"):header.classList.remove("slim")}window.onscroll=function(){slimHeader()()};let current=0;for(var i=0;i<document.links.length;i++)document.links[i].href===document.URL&&(current=i);function toggleMobileNav(){let e=document.getElementById("primary-menu"),t=document.getElementById("menu-toggle");"block"===e.style.display?(e.style.display="none",t.classList.remove("open")):(e.style.display="block",t.classList.add("open"))}document.links[current].className="active";let menuToggle=document.getElementById("menu-toggle"),clickHandler=function(){toggleMobileNav()};menuToggle.addEventListener("click",clickHandler);